(function($) {
  return $('img.scale').imageScale({
    scale: 'best-fill'
  });
})(jQuery);

(function($) {
  if ($('body').hasClass('archive') && $('body').hasClass('tax-region')) {
    $('#search').on('change', 'select.parent', function(event) {
      var $this, region_url, type;
      $this = $(this);
      type = $('#search').attr('class');
      region_url = [Wordpress.home_url, 'regiao', type, $this.val()].join('/');
      $('select.children').css('display', 'none');
      return $.ajax(region_url, {
        complete: function(response, status) {
          var $response;
          if (status === 'error') {
            return;
          }
          $response = $(response.responseText);
          return $('select.children').replaceWith($response.find('select.children'));
        }
      });
    });
    $('#search').on('change', 'select.children', function(event) {
      var $this, parent, region_url, type;
      $this = $(this);
      type = $('#search').attr('class');
      parent = $('select.parent').val();
      region_url = [Wordpress.home_url, 'regiao', type, parent, $this.val()].join('/');
      return window.location.href = region_url;
    });
    return $('#search').on('click', 'input', function(event) {
      return $('#search select.children').change();
    });
  }
})(jQuery);
